import React, { Component } from 'react';
import { Form, Input, Icon, Button } from 'antd';

let id = 0;

class DynamicFieldSet extends Component {
  constructor(props) {
    super(props);
  }

  remove = k => {
    const { form } = this.props;
    // can use data-binding to get
    const keys = form.getFieldValue('keys');
    // We need at least one passenger
    if (keys.length === 1) {
      return;
    }

    // can use data-binding to set
    form.setFieldsValue({
      keys: keys.filter(key => key !== k),
    });
  };

  add = () => {
    const { form } = this.props;
    // can use data-binding to get
    const keys = form.getFieldValue('keys');
    const nextKeys = keys.concat(id++);
    // can use data-binding to set
    // important! notify form to detect changes
    form.setFieldsValue({
      keys: nextKeys,
    });
  };

  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        const { keys, names } = values;
        console.log('Received values of form: ', values);
        console.log('Merged values:', keys.map(key => names[key]));
      }
    });
  };

  render() {
    const input =
      <div>
        <label
          htmlFor="service" > Service :</label>
        <Input name="service"
          onChange={
            this.searchDataService.bind(this)
          }
          placeholder="Rechercher un service"
          value={
            text_service
          }
          className="form-control" />

        {
          (this.state.list_service) ?

            <div className="" >
              <ul id="scroll" style={this.state.scroll_service}>
                {
                  this.state.list_service.map((value, i) => {
                    return (
                      <li key={i} onClick={
                        (e) => this.selectService(value, e)}> {value.Designation_solution}  </li>
                    )

                  })
                } </ul> </div> : null}<br></br>
        <label htmlFor="prix_abo" > Prix :</label>
        <Input name="prix_abo"
          placeholder="prix_abo"
          value={
            price_abo
          }
          onChange={
            this.searchPrice.bind(this)
          }
          className="form-control" />
        <p className="text-danger" style={{ marginTop: '2%' }}>Marge abo : {marge}</p>


      </div>

    const { getFieldDecorator, getFieldValue } = this.props.form;
    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 4 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 20 },
      },
    };
    const formItemLayoutWithOutLabel = {
      wrapperCol: {
        xs: { span: 24, offset: 0 },
        sm: { span: 20, offset: 4 },
      },
    };
    getFieldDecorator('keys', { initialValue: [] });
    const keys = getFieldValue('keys');
    const formItems = keys.map((k, index) => (
      <Form.Item
        {...(index === 0 ? formItemLayout : formItemLayoutWithOutLabel)}
        label={index === 0 ? 'Passengers' : ''}
        required={false}
        key={k}
      >
        {getFieldDecorator(`names[${k}]`, {
          validateTrigger: ['onChange', 'onBlur'],
          rules: [
            {
              required: true,
              whitespace: true,
              message: "Please input passenger's name or delete this field.",
            },
          ],
        })(
          // <Input placeholder="test" style={{ width: '60%', marginRight: 8 }} />
          input
        )}
        {keys.length > 1 ? (
          <Icon
            className="dynamic-delete-button"
            type="minus-circle-o"
            onClick={() => this.remove(k)}
          />
        ) : null}
      </Form.Item>
    ));
    return (
      <Form onSubmit={this.handleSubmit}>
        {formItems}
        <Form.Item {...formItemLayoutWithOutLabel}>
          <Button type="dashed" onClick={this.add} style={{ width: '60%' }}>
            <Icon type="plus" /> Add field
          </Button>
        </Form.Item>
        <Form.Item {...formItemLayoutWithOutLabel}>
          <Button type="primary" htmlType="submit">
            Submit
          </Button>
        </Form.Item>
      </Form>
    );
  }
}

const WrappedDynamicFieldSet = Form.create({ name: 'dynamic_form_item' })(DynamicFieldSet);
export default WrappedDynamicFieldSet;
