import React, {
  Component
} from 'react';
import axios from 'axios';
import loader from '../../assets/loader.gif';
import './devis.css'
import {
  Input, Switch, notification, DatePicker
} from 'antd';
var title = ''
var message = ''
var type = '';

const openNotificationWithIcon = (type) => {
  notification[type]({
    message: title,
    description: message,
    durarion: null
  });
};

export default class InputClient extends Component {
  constructor() {
    super();
    this.state = {
      data: {},
      list: undefined,
      text: "",
      i: 0,
      finish: false,
      address: "",
      scroll: { overflowY: 'scroll'},

      nameSignataire: '',
      refTech: '',
      contactFact: '',

      data_final: [],
      switchBool: false,

      dateCommande: '',
      dureSous: '',
    }
  }

  componentDidMount() {
    var data = [];

    var start = 0;
    var { i } = this.state
    for (i = 0; i < 10; i++) {
      axios.get(`https://sayse.pipedrive.com/v1/deals?api_token=d168467b213a71acdc4ba5cb5b2cfe96529ac061&start=` + start + `&limit=500`)
        .then(res => {
          // console.log(`https://sayse.pipedrive.com/v1/deals?api_token=d168467b213a71acdc4ba5cb5b2cfe96529ac061&start=` + start + `&limit=500`)

          if (res.data.data !== null) {

            res.data.data.forEach(element => {
              if (element.org_id === null) {
                return false;
              }
              else {

                // data.push(element.org_id)
                data.push({ name: element.org_id.name, adresse: element.org_id.address });

                // console.log(element.org_id)
                //   this.setState(() => ({
                //     data: {
                //         ...this.state.data,
                //         org_name: element.org_id,
                //     },
                // }))

              }
            });

          }
          if (i === 10) {
            // console.log(this.getUnique(data,'name'))
            this.setState({ data: this.getUnique(data, 'name') })
            // console.log(this.state.data)
            return;
          }


        })
      start += 500;
    }
  }


  getUnique(arr, comp) {

    const unique = arr
      .map(e => e[comp])

      // store the keys of the unique objects
      .map((e, i, final) => final.indexOf(e) === i && i)

      // eliminate the dead keys & store unique objects
      .filter(e => arr[e]).map(e => arr[e]);

    return unique;

  }


  select(value) {

    this.setState({
      text: value.name,
      address: value.adresse,
      list: [],
      scroll: { overflowY: 'scroll' }
    }
    )

    return (
      null
    );
  }

  searchData(e) {
    // console.log(this.state.data.length)

    // var err = [{
    //   name: "La recherche n'a retourner aucun resultat",
    //   adresse: '',
    // }]

    var queryData = [];
    if (e.target.value !== '') {
      this.setState({ scroll: { overflowY: 'scroll', height: '300px', width: '540px' } })

      // this.state.data.forEach(function (person) {
      for (let i = 0; i < this.state.data.length; i++) {
        // console.log(this.state.data[i].name);

        if (this.state.data[i].name !== null) {
          if (this.state.data[i].name.trim().toLowerCase().indexOf(e.target.value) !== -1) {


            // if (queryData.length < 10) {
            queryData.push(this.state.data[i]);
            // }

          }

        }
      }
      // });

    }
    else {
      this.setState({ scroll: { overflowY: 'scroll' } })
    }

    this.setState({
      list: queryData
    });
    this.setState({
      text: e.target.value
    })



  }
  searchAddress(e) {
    this.setState({
      address: e.target.value
    })
  }

  nameSignataire(e) {
    this.setState({
      nameSignataire: e.target.value
    })

  }

  refTech(e) {
    this.setState({
      refTech: e.target.value
    })
  }

  contactFact(e) {
    this.setState({
      contactFact: e.target.value
    })
  }



  onChangeDate(date, dateString) {
    this.setState({
      dateCommande: dateString
    })
  }

  dureSous(e) {
    this.setState({
      dureSous: e.target.value
    })
  }

  SwitchClick(checked, e) {
    // console.log(this.state.dureSous);


    var data = {};

    data.client = this.state.text
    data.adresse = this.state.address
    data.dateCom = this.state.dateCommande
    data.dureSous = this.state.dureSous
    data.nameSignataire = this.state.nameSignataire
    data.refTech = this.state.refTech
    data.contactFact = this.state.contactFact


    if (checked === true) {

      if (data.client.length !== 0 && data.adresse.length !== 0 && data.nameSignataire.length !== 0 && data.refTech.length !== 0 && data.contactFact.length !== 0 && data.dateCom.length !== 0 && data.dureSous.length !== 0) {

        type = "success"
        title = "Bravo !"
        message = "Les informations des champs ont été enregistrées avec succès"
        this.setState({
          data_final: data,
          switchBool: true

        }
          , () => {
            this.props.getData(this.state.data_final)
          }
        )
      }
      else {
        type = "error"
        title = "Erreur !"
        message = "Veuillez remplir tout les champs avant de les valider"
        this.setState({
          switchBool: false,
        })


      }
    }
    else {
      type = "warning"
      title = "Attention !"
      message = "Vos modifications ont déja été enregistrées"
    }



    // console.log(this.state.switchBool);

    e.preventDefault();
    e.stopPropagation();
  }

  render() {
    const { text } = this.state
    const { address } = this.state

    var { data } = this.state
    // console.log(data)
    const { nameSignataire, refTech, contactFact, switchBool } = this.state;


    if (data.length < 1000 || data.length === undefined) {
      return (
        <div className="container text-center" style={{ marginTop: '5%' }}>
          <img src={loader} alt='Chargement' width='20%'></img>
        </div>
      )
    }

    else {

      return (
        <div className="" style={{ marginTop: "2%" }}>
          <div className="AutocompleteText row " >
            <div className="col-6">
              <label
                htmlFor="client" className="label"> Client :</label>
              <Input name="client"
                onChange={
                  this.searchData.bind(this)
                }
                placeholder="Rechercher un client"
                value={
                  text
                }
                className="form-control" />
              {
                (this.state.list) ?

                  <div >
                    <ul style={this.state.scroll}>
                      {
                        this.state.list.map((value, i) => {
                          return (
                            <li key={i} onClick={
                              () => this.select(value)}> {value.name} : {value.adresse}  </li>
                          )

                        })
                      } </ul> </div> : null}</div>
            <div className="col-6">
              <label
                htmlFor="address" className="label" > Adresse :</label>
              <Input name="address"

                placeholder="Rechercher une addresse"
                value={
                  address
                }
                onChange={
                  this.searchAddress.bind(this)
                }
                className="form-control" />

            </div>
          </div>
          <div className="row">
            <div className="col-6">
              <label
                htmlFor="date_start" className="label" > Date de commande :</label><br></br>
              <DatePicker onChange={this.onChangeDate.bind(this)} style={{ width: "100%" }} />
            </div>
            <div className="col-6">
              <label
                htmlFor="dureSous" className="label" > Durée de souscription (en mois):</label><br></br>
              <Input type="number" name="tentacles"
                min="1" max="100" placeholder="Durée de souscription (en mois)"
                value={
                  this.state.dureSous
                }
                onChange={
                  this.dureSous.bind(this)
                }
              />
            </div>

            <div className="col-3"></div>

          </div>

          <div className="row" style={{ marginTop: "2vh", marginBottom: "2vh" }}>
            <div className="col-6">
              <label className="label">Nom du Signataire : </label>
              <Input name="nameSignataire"
                placeholder="Nom du Signataire"
                value={
                  nameSignataire
                }
                onChange={
                  this.nameSignataire.bind(this)
                }
                className="form-control" />
            </div>
            <div className="col-6">
              <label className="label">Nom du Référent technique : </label>
              <Input name="refTech"
                placeholder="Référent technique"
                value={
                  refTech
                }
                onChange={
                  this.refTech.bind(this)
                }
                className="form-control" />
            </div>
          </div>
          <div className="row">
          <div className="col-4"></div>
            <div className="col-4 text-center">
              <label className="label">Nom du Contact pour la facturation : </label>
              <Input name="contactFact"
                placeholder="Contact facturation"
                value={
                  contactFact
                }
                onChange={
                  this.contactFact.bind(this)
                }
                className="form-control" />
            </div>
          </div>
          <div className="row">
            <div className="col-3">
            </div>
            <div className="col-6 text-center">
            <hr></hr>

              <p className="p">Valider les informations pour le client {this.state.text}</p>
              <Switch onClick={() => openNotificationWithIcon(type)} checked={switchBool} onChange={this.SwitchClick.bind(this)} style={ switchBool ? {backgroundColor: '#d6ae7b'} : {backgroundColor: ''} }/>
            </div>
            <div className="col-3">
            </div>
          </div>
        </div>

      )
    }
  }
}
