import React, { Component } from 'react';
import axios from 'axios';
import './test.css'

export default class Test extends Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      suggestions: [],
      text: '',
    }
  }

  componentDidMount() {

    axios.get(`https://sayse.pipedrive.com/v1/deals?api_token=d168467b213a71acdc4ba5cb5b2cfe96529ac061&limit=500`)
      .then(res => {
        var data = [];

        res.data.data.forEach(element => {
          data.push(element.person_name)
        });
        // console.log([...new Set(data)]);

        data = data.filter((v, i, arr) => arr.indexOf(v) === i)

        this.setState({ items: data })
        console.log(res.data.data)

      });

  }

  onTextChange = (e) => {
    const { items } = this.state

    const value = e.target.value;
    let suggestions = [];

    if (value.length > 0) {
      const regex = new RegExp(`^${value}`, 'i');
      suggestions = items.sort().filter(v => regex.test(v));
    }

    this.setState({ suggestions, text: value })

  }
  renderSuggestions() {
    const { suggestions } = this.state;

    if (suggestions.length === 0) {
      return null;
    }
    return (
      <ul>
        {suggestions.map((item, i) => <li onClick={() => this.suggestionSelect(item)} key={i} >{item}</li>)}
      </ul>
    );
  }

  suggestionSelect(value) {
    this.setState({
      text: value,
      suggestions: [],
    })
  }

  render() {
    const { text } = this.state

    return (
      <div className="container">
        <div className="row">
          <div className="col-6"></div>
          <div className="col-6">
            <label>Choisir un client</label>
            <div className='AutocompleteText '>
              <input value={text} className="form-control" onChange={this.onTextChange} type="text" />
              <ul>
                {this.renderSuggestions()}
              </ul>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
