import React, { Component } from 'react';
import axios from 'axios';
import './test.css'

export default class App extends Component {
    constructor() {
        super();
        this.state = {
            data: [],
            list: undefined,
            text: "",
        }
    }

    componentDidMount() {

        axios.get(`https://sayse.pipedrive.com/v1/deals?api_token=d168467b213a71acdc4ba5cb5b2cfe96529ac061&limit=500`)
            .then(res => {
                var data = [];

                res.data.data.forEach(element => {
                    data.push(element.person_name)
                });
                // console.log([...new Set(data)]);

                data = data.filter((v, i, arr) => arr.indexOf(v) === i)

                this.setState({ data: data })

            });

    }

    select(value) {
        // e.preventDefault();
        console.log(value)
        this.setState({ text: value, list: [] })

        return (
            null
        );
    }

    searchData(e) {
        var queryData = [];
        if (e.target.value !== '') {
            this.state.data.forEach(function (person) {
                if(person !== null){
                    console.log(person.toLowerCase())
                }


                // if (person.toLowerCase().indexOf(e.target.value) !== -1) {
                //     if (queryData.length < 10) {
                //         queryData.push(person);
                //     }
                // }
            });

        }
        this.setState({ list: queryData });
        this.setState({ text: e.target.value })

    }
    render() {
        const { text } = this.state
        return (
            <div className="AutocompleteText">
                <input onChange={this.searchData.bind(this)} placeholder="Search Pokemon" value={text} className="search-input" />
                {(this.state.list) ?
                    <div>
                        <ul>
                            {this.state.list.map((value, i) => {
                                return (
                                    <li key={i} onClick={() => this.select(value)}>
                                        {value}
                                    </li>
                                )
                            })}
                        </ul>
                    </div> : null}
            </div>
        )
    }
}
