import React, {
    Component
  } from 'react';
  import axios from 'axios';
  import loader from '../../assets/loader.gif';
  import './devis.css'
  import {
    Input
  } from 'antd';

  import obj from '../../assets/data.json'

  export default class InputService extends Component {
    constructor() {
        super();
        this.state = {
          data: [],
          list: undefined,
          text: "",
          price_abo: '',
        }
      }

      componentDidMount()
      {
        this.setState({data: obj}, () => {

        })

      }

      searchData(e) {
        // console.log(this.state.data.length)


        var queryData = [];
        if (e.target.value !== '') {
          // this.state.data.forEach(function (person) {
          for (let i = 0; i < this.state.data.length; i++) {

            if (this.state.data[i].Designation_solution !== null) {
              if (this.state.data[i].Designation_solution.toLowerCase().indexOf(e.target.value) !== -1) {


                if (queryData.length < 15) {
                  queryData.push(this.state.data[i]);
                }

              }

            }
          }

        }

        this.setState({
          list: queryData
        });
        this.setState({
          text: e.target.value
        })  

      }

      select(value) {

        this.setState({
          text: value.Designation_solution,
          price_abo: value.Abo_mois_ht,
          list: []
        })

        return (
          null
        );
      }


      searchPrice(e) {
        this.setState({
            price_abo: e.target.value
        })
      }


      render(){
          console.log(this.state.data)
        const { text } = this.state
        const {price_abo} = this.state

        return (
        <div className="AutocompleteText" >
        <div className='row'>
        <div className="col-6">
          <label
            htmlFor="service" > Service </label>
          <Input name="service"
            onChange={
              this.searchData.bind(this)
            }
            placeholder="Rechercher un service"
            value={
              text
            }
            className="form-control" />
            </div>
          {
            (this.state.list) ?

              <div >
                <ul>
                  {
                    this.state.list.map((value, i) => {
                        console.log(value)
                      return (
                        <li key={i} onClick={
                          () => this.select(value)}> {value.Designation_solution}  </li>
                      )

                    })
                  } </ul> </div> : null}
                          <div className="col-6">

                   <Input name="prix_abo"
                        placeholder="prix_abo"
                        value={
                        price_abo
                        }
                        onChange={
                        this.searchPrice.bind(this)
                        }
                        className="form-control" />

                  </div></div></div>
        )
      }
  }
