handleSearch = (value) => {
    const url = `https://sayse.pipedrive.com/v1/deals?api_token=d168467b213a71acdc4ba5cb5b2cfe96529ac061&limit=500`;
    this.setState({ search: value }, () => {
        if (this.state.search) {
            axios.get(url)
                .then(this.setState({ data: [] }))
                .then(response => {
                    function filterByValue(array, string) {
                        return array.filter(o =>
                            Object.keys(o).some(k => o[k].toString().toLowerCase().includes(string.toLowerCase())));
                    }
                    this.setState({ client: filterByValue(response.data, this.state.search) });
                })
        }
        else {
            axios.get(url)
                .then(response => {
                    this.setState({ data: response.data })
                    console.log(this.state.data)
                })
        }

    this.setState({
      dataSource: !value ? [] : [
        value,
        value + value,
        value + value + value,
      ],
    });
  })
}

  render() {
    const { dataSource } = this.state;
    return (
    <div className="container">
      <AutoComplete
        dataSource={dataSource}
        style={{ width: 200 }}
        onSelect={onSelect}
        onSearch={this.handleSearch}
        // onChange={this.handleChange(this.state.search).bind(this)}
        // value={this.state.search}
        placeholder="input here"
      />
      </div>
    );
  }
}
