import React, { Component } from 'react';
import InputClient from '../components/Devis/inputClient.js';
import InputAdress from '../components/Devis/inputAdress.js';

import { DatePicker,Select, Option } from 'antd';
import moment from 'moment';

const dateFormat = 'YYYY/MM/DD';


export default class Devis extends Component {


    componentDidMount() {

    }

    render() {

        return (
            <div className="container">
                <div className='row' style={{ marginTop: '2%' }}>
                <div className="col-3">
                    <label for="date_end">Date de fin</label>

                    <DatePicker name="date_end" defaultValue={moment('2019/01/01', dateFormat)} format={dateFormat} />
                    </div>

                    <div className="col-3">
                    <label for="devise">Devise</label><br></br>
                    <Select
                        style={{ width: 200 }}
                        placeholder="Choisir une devise">

                        <option value="euros">Euros</option>
                        <option value="dollar">Dollar</option>
                        <option value="livre_sterling">Livre Sterling</option>
                        <option value="yen">Yen</option>
                    </Select>
                    </div>
                    <div className="col-3">
                    <label for="date_start">Date de debut</label>
                        <DatePicker name="date_start" defaultValue={moment('2019/01/01', dateFormat)} format={dateFormat} />
                    </div>
                    <div className="col-3">
                    <label for="date_start">Date de debut</label>
                        <DatePicker name="date_start" defaultValue={moment('2019/01/01', dateFormat)} format={dateFormat} />
                    </div>
                    </div>
                    <div className='row' style={{ marginTop: '2%' }}>
                    <div className="col-3"><InputClient /></div>
                    <div className="col-3"><InputAdress /></div>
                    </div>
                </div>
        )
    }
}
