import React, { Component } from 'react';
import logo from '../../assets/logo_byos_yellow.png';
import { Link } from 'react-router-dom'
import './home.css'


export default class Home extends Component {


    componentDidMount() {
        const signUpButton = document.getElementById('signUp');
        const signInButton = document.getElementById('signIn');
        const container = document.getElementById('container');

        signUpButton.addEventListener('click', () =>
        container.classList.add('right-panel-active'));

        signInButton.addEventListener('click', () =>
        container.classList.remove('right-panel-active'));

    }


    render() {

        return (
            // <div className="body">
            //     <div className="container ">

            //         <div className="col-sm-offset-1 col-sm-12 text-center">
            //             <img src={logo} alt='logo_byos' width="70%" style={{ marginTop:'8%'}}></img>
            //         </div>

            //     <div className="row">
            //         <div className="col-sm-offset-1 col-sm-4 text-center"></div>
            //             <div className="col-sm-offset-1 col-sm-4 text-center">
            //                 <Link className="btn btn-lg btn-block btn_devis" to='/devis'>Cr√©er un devis</Link>
            //             </div>
            //         </div>
            //     </div>
            // </div>
        // <div>


<div>
<div class="container" id="container">
        <div class="form-container sign-up-container">
            <form action="#">
                <h1 className="h1">Create Account</h1>
                <div class="social-container">
                    <a href="#" class="social a"><i class="fab fa-facebook-f"></i></a>
                    <a href="#" class="social a"><i class="fab fa-google-plus-g"></i></a>
                    <a href="#" class="social a"><i class="fab fa-linkedin-in"></i></a>
                </div>
                <span>or use your email for registration</span>
                <input type="text" placeholder="Name" />
                <input type="email" placeholder="Email" />
                <input type="password" placeholder="Password" />
                <button className="button">Sign Up</button>
            </form>
        </div>
        <div class="form-container sign-in-container">
            <form action="#">
                <h1 className="h1">Sign in</h1>
                <div class="social-container">
                    <a href="#" class="social a"><i class="fab fa-facebook-f"></i></a>
                    <a href="#" class="social a"><i class="fab fa-google-plus-g"></i></a>
                    <a href="#" class="social a"><i class="fab fa-linkedin-in"></i></a>
                </div>
                <span>or use your account</span>
                <input type="email" placeholder="Email" />
                <input type="password" placeholder="Password" />
                <a href="#" className="a">Forgot your password?</a>
                <button className="button">Sign In</button>
            </form>
        </div>

        <div class="overlay-container">
            <div class="overlay">
                <div class="overlay-panel overlay-left">
                    <h1 className="h1">Welcome Back!</h1>
                    <p className="p">To keep connected with us please login with your personal info</p>
                    <button class="ghost button" id="signIn">Sign In</button>
                </div>
                <div class="overlay-panel overlay-right">
                    <h1 className="h1">Hello, Friend!</h1>
                    <p className="p">Enter your personal details and start journey with us</p>
                    <button class="ghost button" id="signUp">Sign Up</button>
                </div>
            </div>
        </div>
    </div>
</div>
        )
    }
}
