import React, { Component } from 'react';
import InputClient from '../components/Devis/inputClient.js';
import InputAdress from '../components/Devis/inputAdress.js';

import { DatePicker,Select } from 'antd';
import moment from 'moment';

const dateFormat = 'YYYY/MM/DD';


export default class Devis extends Component {


    componentDidMount() {

    }

    render() {

        return (
            <div className="container">
                <div className='row' style={{ marginTop: '2%' }}>
                    <div className="col-3"><InputClient /></div>
                    <div className="col-3"><InputAdress /></div>
                    <div className="col-3 ">
                    <Select
                        showSearch
                        style={{ width: 200 }}
                        placeholder="Select a person"
                        optionFilterProp="children"
                        onChange={onChange}
                        onFocus={onFocus}
                        onBlur={onBlur}
                        onSearch={onSearch}
                        filterOption={(input, option) => option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0}
                    >
                        <Option value="jack">Jack</Option>
                        <Option value="lucy">Lucy</Option>
                        <Option value="tom">Tom</Option>
                    </Select>
                    </div>
                    <div className="col-3">
                        <DatePicker defaultValue={moment('2019/01/01', dateFormat)} format={dateFormat} />
                    </div>
                    </div>
                </div>
        )
    }
}
