import React, { Component } from 'react';
import FinalForm from './test_input';
import Client from '../components/Devis/inputClient';
import DetailClient from '../components/Devis/DetailClient';

import { Form, Icon, Button, Input } from 'antd';

let id = 0;

class NotFound extends Component {
    constructor() {
        super();
        this.state = {
            res: [],
            client: [],
            nameSite: '',
            detail: [],
        }
    }

    componentDidMount() {

    }

    add = () => {
        const { form } = this.props;
        // can use data-binding to get
        const keys = form.getFieldValue('keys');
        const nextKeys = keys.concat(id++);
        // can use data-binding to set
        // important! notify form to detect changes
        form.setFieldsValue({
            keys: nextKeys,
        });
    };

    NameSiteChange(e){
        this.setState({ nameSite: e.target.value})
    }

    getData(e) {
        this.setState({ client: [... this.state.client, e] })
    }

    getDetail(e) {
        this.setState({ detail: [... this.state.detail, e] })
    }

    handleSubmit(event) {
        console.log(this.state.res);
        console.log(this.state.client);
        console.log(this.state.nameSite);

        event.preventDefault();
    }

    getDataForPdf = (e) => {
        this.setState({ res: [... this.state.res, e] })
    }

    render() {
        const { getFieldDecorator, getFieldValue } = this.props.form;
        const formItemLayout = {
            labelCol: {
                xs: { span: 24 },
                sm: { span: 4 },
            },
            wrapperCol: {
                xs: { span: 24 },
                sm: { span: 20 },
            },
        };
        const formItemLayoutWithOutLabel = {
            wrapperCol: {
                xs: { span: 24, offset: 0 },
                sm: { span: 20, offset: 4 },
            },
        };


        getFieldDecorator('keys', { initialValue: [] });
        const keys = getFieldValue('keys');
        const formItems = keys.map((k, index) => (
            <div key={k}>
                <Form.Item className="row" style={{ marginTop: '1%' }}
                    // {...(index === 0 ? formItemLayout : formItemLayoutWithOutLabel)}
                    // label={index === 0 ? '' : ''}
                    required={false}
                    key={k}
                >
                    {/* {getFieldDecorator(`names[${k}]`, {
                        validateTrigger: ['onChange', 'onBlur'],
                        rules: [
                            {
                                required: true,
                                whitespace: true,
                                message: "Veuillez remplir tout les champs avant de pouvoir créer le devis",
                            },
                        ],
                    })
                        ( */}
                        <div>
                            <hr></hr>
                            <div className="row">
                                <div className="col-4"></div>

                                <div className="col-4">
                                <label className="p">Nom du site : </label>
                                    <Input
                                        className="form-control"
                                        type="text"
                                        value={this.state.nameSite}
                                        placeholder="Nom du site"
                                        onChange={this.NameSiteChange.bind(this)}
                                    />                                </div>
                                <div className="col-4"></div>

                                <FinalForm getDataForPdf={this.getDataForPdf.bind(this)} />
                            </div>
                        </div>
                        {/* )} */}
                    {keys.length > 1 ? (
                        <div className="text-center">
                            {/* <p className="p delete">Supprimer le service n° {k + 1} &nbsp;
                  <Icon
                      className="dynamic-delete-button"
                      type="minus-circle-o"
                      onClick={() => this.remove(k)}
                    />
                  </p> */}
                        </div>
                    ) : null}
                </Form.Item>
            </div>

        ));

        return (
            <div className="container">
                <form onSubmit={this.handleSubmit.bind(this)}>
                    <Client getData={this.getData.bind(this)} />
                    <DetailClient getDetail={this.getDetail.bind(this)} />
                    {/* <FinalForm getDataForPdf={this.getDataForPdf.bind(this)} /> */}

                    <div className="">
                        <div className="">
                            {formItems}
                            {/* <button onClick={this.getKey.bind(this)}>getKey</button> */}
                        </div>
                        <div className="text-center">
                            <Button type="dashed" className="btn_add" onClick={this.add} style={{ width: '20%', marginBottom: '2vh', marginTop: '2vh' }}>
                                {/* <Icon type="plus" /> */}
                                Ajouter un nouveau site
                        </Button>
                        </div>
                    </div>
                    <div className="text-center">
                        <input className="btn btn-light btn-test" type="submit" value="Convertir en PDF" style={{ marginTop: '5%', marginBottom: '5%' }} />
                    </div>
                </form>
            </div>
        )
    }
}
const WrappedDynamicFieldSet = Form.create({ name: 'dynamic_form_item' })(NotFound);
export default WrappedDynamicFieldSet;
