import React, { Component } from 'react';
import data from '../assets/codebeautify.json'
import { Preview, print } from 'react-html2pdf';
import logo from '../assets/logo_sayse.png';


export default class GetData extends Component {

    constructor() {
        super();
        this.state = {
            client: [],
            service: [],

            test_client: [

            ],
            test_service : [

            ]
        }
    }


    componentDidMount() {
        var array = [];

        if (this.props.text.length === 0) {
            this.props.history.push('/ok')
        }
        else {
            this.setState({
                client: this.props.text.client[0]
            })

            for (let i = 0; i < this.props.text.service.length; i++) {
                array.push(this.props.text.service[i])
            }
            if (array.length !== 0) {
                this.setState({
                    service: array
                })
            }
        }


    }
    download() {
        print('Devis', 'PdfDevis')
    }

    render() {

        console.log(this.state.client);
        console.log(this.state.service);

        return (
            <div className="container-fluid">
                <Preview id={'PdfDevis'}>
                    <div className="container">
                        <div className="row">
                            <div className="col-4">
                                <img src={logo} alt="logo_sayse" width="60%"></img>
                            </div>
                            <div className="col-3 text-center text-danger mt-3">
                                <p className="p_head">Devis pour solutions</p>
                            </div>
                            <div className="col-5 text-center text-danger mt-3">
                                <p className="p_head">SDWAN / ACCES INTERNET (By SAYSE)</p>
                            </div>
                        </div>
                        <hr></hr>

                        <div className="row">
                            <div className="col-5">
                                <p style={{ fontSize: '8px' }}>
                                    SAYSE SAS au capital social de 17 566 Euros<br></br>
                                    RCS de Paris 801 891 722 00010<br></br>
                                    N° de TVA Intracommunautaire FR 03 801 891 722<br></br>
                                    Siège social : 10 rue de la Paix - 75002 PARIS<br></br>
                                    Etablissement secondaire : 86-88 rue Thiers - 92100 Boulogne Billancourt<br></br>
                                    Tel : 0820.432.000 - www.sayse.fr - contact@sayse.fr<br></br>
                                </p>



                            </div>
                            <div className="col-4" style={{ borderLeftWidth: 100, borderColor: 'black' }}>

                            </div>
                            <div className="col-3"></div>

                        </div>
                    </div>
                </Preview>
                <div className="row">
                    <div className="col-3"></div>
                    <div className="col-6 text-center" style={{ marginTop: '75vh' }}>
                        <button className="degrade-btn btn" onClick={this.download.bind(this)}> Telecharger</button>
                    </div>
                    <div className="col-3"></div>
                </div>

            </div>
        );
    }
}

