export default class Test extends Component {
  constructor(props) {
    super(props);
    this.state = {
      rows: [
        { value: '', checked: false },
      ]
    };
  }




  searchDataService(e) {
    //indexOf(e.target.value) !== -1
    var queryData = [];


    if (e.target.value !== '') {
        this.setState({ scroll_service: { overflowY: 'scroll', height: '250px', width: '265px' } })

        // this.state.data.forEach(function (person) {
        for (let i = 0; i < this.state.data_service.length; i++) {

            if (this.state.data_service[i].Designation_solution !== null) {
                if (this.state.data_service[i].Designation_solution.trim().toLowerCase().match(e.target.value)) {

                    // if (this.state.data[i].Designation_solution.trim().toLowerCase().indexOf(e.target.value) !== -1) {
                    // if (queryData.length < 15) {
                    queryData.push(this.state.data_service[i]);

                    // }
                    // }
                }

            }
        }

    }
    else {
        this.setState({ scroll_service: { overflowY: 'scroll' } })
    }

    this.setState({
        list_service: queryData
    });
    this.setState({
        text_service: e.target.value
    })


}




  updateValue(e, idx) {
    const rows = this.state.rows;
    rows[idx].value = e.target.value;
    this.setState({
      rows,
    });
  }

  onChecked(idx) {
    const rows = this.state.rows;
    rows[idx].checked = !rows[idx].checked;
    this.setState({
      rows,
    });
  }

  addRow() {
    const rows = [...this.state.rows,
    { value: '', checked: false }
    ];
    this.setState({
      rows,
    });
  }

  deleteRows() {
    this.setState({
      rows: this.state.rows.filter((e, i) => !e.checked)
    });
  }

  render() {

    const Row = function (props) {
      const { checked, value, onChange, onChecked } = props;
      return (
        <div>
          <input
            type="checkbox"
            checked={checked}
            onChange={onChecked}
          />
          <div className="col-5">
            <label
              htmlFor="service" > Service :</label>
            <Input name="service"
              onChange={
                this.searchDataService.bind(this)
              }
              placeholder="Rechercher un service"
              value={
                this.state.text_service
              }
              className="form-control" />

            {
              (this.state.list_service) ?

                <div className="" >
                  <ul id="scroll" style={this.state.scroll_service}>
                    {
                      this.state.list_service.map((value, i) => {
                        return (
                          <li key={i} onClick={
                            (e) => this.selectService(value, e)}> {value.Designation_solution}  </li>
                        )

                      })
                    } </ul> </div> : null}<br></br></div>
          <div className="col-5">
            <label htmlFor="prix_abo" > Prix :</label>
            <Input name="prix_abo"
              placeholder="prix_abo"
              value={
                this.state.price_abo
              }
              onChange={
                this.searchPrice.bind(this)
              }
              className="form-control" />
            <p className="text-danger" style={{ marginTop: '2%' }}>Marge abo : { this.state.marge}</p>
          </div>
          </div>
          );
        }

        return(
      <div className="container">
            <h4 className="text-center">Formulaire</h4>
            {this.state.rows.map((row, idx) => {
              return (
                <Row
                  key={idx}
                  value={row.value}
                  checked={row.checked}
                  onChange={(e) => this.updateValue(e, idx)}
                  onChecked={() => this.onChecked(idx)}
                />
              )
            })
            }
            <button onClick={() => this.addRow()}>
              add
        </button>
            <button onClick={() => this.deleteRows()}>
              delete
        </button>
          </div>
          );
        }
      }

      // ReactDOM.render(
