import React, { Component } from 'react';
import Client from '../components/Devis/inputClient';
import Service from './test.js';
import { Input, Icon, Switch } from 'antd';
import ReactGoogleMapLoader from "react-google-maps-loader"
import ReactGooglePlacesSuggest from "react-google-places-suggest"

const API_KEY = "AIzaSyAQU3x2PDxhLZEYFk4Af4HBPYDpy40aHEs"
var obj = {}

export default class Test extends Component {
    constructor() {
        super();
        this.state = {
            client: [],
            service: [],

            search: "",
            value: "",

            name: '',
            tel: '',
            mail: '',

            ipWan: "",
            ipLan: "",
            MasqueReseau1: '',
            MasqueReseau2: '',
            nameSite: '',

            data_final: [],

        }
    }

    componentDidMount() {
        // console.log(this.state.service);
        // console.log(this.state.client);

        // obj.service = this.state.service
        // obj.value = this.state.value
        // obj.name = this.state.name
        // obj.tel = this.state.tel
        // obj.mail = this.state.mail
        // obj.service.ipWan = this.state.ipWan
        // obj.service.ipLan = this.state.ipLan
        // obj.service.MasqueReseau1 = this.state.MasqueReseau1
        // obj.service.MasqueReseau2 = this.state.MasqueReseau2


        // console.log(this.state.service);





    }

    Submit(e) {


        // obj.client = this.state.client
        obj.service = this.state.service
        obj.livraisonAdresse = this.state.value
        obj.name = this.state.name
        obj.tel = this.state.tel
        obj.mail = this.state.mail
        obj.service.ipWan = this.state.ipWan
        obj.service.ipLan = this.state.ipLan
        obj.service.MasqueReseau1 = this.state.MasqueReseau1
        obj.service.MasqueReseau2 = this.state.MasqueReseau2
        obj.service.nameSite = this.state.nameSite




        // console.log(this.state.service.length);
        // console.log(this.state.value);
        // console.log(this.state.tel);
        // console.log(this.state.mail);
        // console.log(this.state.ipWan);
        // console.log(this.state.ipLan);
        // console.log(this.state.MasqueReseau1);
        // console.log(this.state.MasqueReseau2);

        // console.log(this.state.client);
        // console.log(this.state.service);
        // console.log(this.state.search);
        // console.log(this.state.value);
        // console.log(this.state.name);
        // console.log(this.state.tel);
        // console.log(this.state.mail);
        this.setState({
            data_final: obj
        }
            , () => {
                this.props.getDataForPdf(obj)
        })
        console.log(obj);
        e.preventDefault();

    }


    getData(e) {
        this.setState({ client: [... this.state.client, e] })
    }
    getService(e) {
        this.setState({ service: [... this.state.service, e] })
        // console.log(this.state.service);
    }


    handleInputChange(e) {
        this.setState({ search: e.target.value, value: e.target.value })
    }

    handleSelectSuggest(suggest) {
        // console.log(suggest)
        this.setState({ search: "", value: suggest.formatted_address })
    }


    NameChange(e) {
        this.setState({ name: e.target.value })
    }


    TelChange(e) {
        this.setState({ tel: e.target.value })
    }

    MailChange(e) {
        this.setState({ mail: e.target.value })
    }


    ipWanChange(e) {
        this.setState({ ipWan: e.target.value })
    }

    ipLanChange(e) {
        this.setState({ ipLan: e.target.value })
    }

    MasqueReseau1(e) {
        this.setState({ MasqueReseau1: e.target.value })
    }

    MasqueReseau2(e) {
        this.setState({ MasqueReseau2: e.target.value })
    }

    allDataComplete() {
        var obj = {}

        obj.service = this.state.service
        obj.value = this.state.value
        obj.name = this.state.name
        obj.tel = this.state.tel
        obj.mail = this.state.mail
        obj.service.ipWan = this.state.ipWan
        obj.service.ipLan = this.state.ipLan
        obj.service.MasqueReseau1 = this.state.MasqueReseau1
        obj.service.MasqueReseau2 = this.state.MasqueReseau2


        // this.setState({
        //     data_final: obj
        // }
        //     , () => {
        //         this.props.getDataForPdf(obj)
        //     })
    }

    SwitchClick(checked, e) {
        // console.log(`switch to ${checked}`);
        // console.log(checked);
        if (checked === true) {
            this.Submit(e);
        }

    }

    NameSiteChange(e) {
        this.setState({ nameSite: e.target.value })
    }

    render() {
        const { search, value, name, tel, mail, ipWan, ipLan, MasqueReseau1, MasqueReseau2 } = this.state

        return (
            <div className="">
                <div className=" container">
                    {/* <form onSubmit={this.Submit.bind(this)}> */}
                    {/* <Client getData={this.getData.bind(this)} /> */}

                    <Service getService={this.getService.bind(this)} />
                    <hr></hr>
                    <div className="row">
                        <div className="col-4"></div>
                        <div className="col-4">
                        <p className="p">Nom du site :</p>
                            <Input
                                className="form-control"
                                type="text"
                                value={this.state.nameSite}
                                placeholder="Nom du site"
                                onChange={this.NameSiteChange.bind(this)}
                            />
                        </div>
                        <div className="col-4"></div>
                    </div>
                    <hr></hr>

                    <div className="row">
                        <div className="col-12">
                            {/* <p className="text-center p text-danger oblg">OBLIGATOIRE !</p> */}

                            <div className="row" style={{ marginBottom: "3%" }}>
                                <div className="col-5">
                                    <label className="p">Adresse de livraison : </label>
                                    <ReactGoogleMapLoader
                                        params={{
                                            key: API_KEY,
                                            libraries: "places,geocode",
                                        }}
                                        render={googleMaps =>
                                            googleMaps && (
                                                <div>
                                                    <ReactGooglePlacesSuggest
                                                        autocompletionRequest={{ input: search }}
                                                        googleMaps={googleMaps}
                                                        onSelectSuggest={this.handleSelectSuggest.bind(this)}
                                                    >
                                                        <Input
                                                            type="text"
                                                            value={value}
                                                            placeholder="Search a location"
                                                            onChange={this.handleInputChange.bind(this)}
                                                        />
                                                    </ReactGooglePlacesSuggest>
                                                </div>
                                            )
                                        }
                                    />
                                </div>
                                <div className="col-2">
                                    <label className="p">Nom du contact : </label>
                                    <Input
                                        type="text"
                                        value={name}
                                        placeholder="Nom du contact"
                                        onChange={this.NameChange.bind(this)}
                                    />
                                </div>
                                <div className="col-2">
                                    <label className="p">Telephone du contact : </label>
                                    <Input
                                        type="tel"
                                        value={tel}
                                        placeholder="Tel du contact"
                                        onChange={this.TelChange.bind(this)}
                                    />
                                </div>
                                <div className="col-2">
