  import './devis.css'
  import {
    Input
  } from 'antd';

  import obj from '../../assets/codebeautify.json'

  export default class InputService extends Component {
    constructor() {
        super();
        this.state = {
          data: [],
          list: undefined,
          text: "",
        }
      }

      componentDidMount()
      {
        this.setState({data: obj}, () => {

        })

      }

      searchData(e) {
        // console.log(this.state.data.length)


        var queryData = [];
        if (e.target.value !== '') {
          // this.state.data.forEach(function (person) {
          for (let i = 0; i < this.state.data.length; i++) {
            console.log(this.state.data[i]);


            if (this.state.data[i].name !== null) {
              if (this.state.data[i].name.toLowerCase().indexOf(e.target.value) !== -1) {


                if (queryData.length < 10) {
                  queryData.push(this.state.data[i]);
                }

              }

            }
          }

        }

        this.setState({
          list: queryData
        });
        this.setState({
          text: e.target.value
        })



      }


      render(){
        //   console.log(this.state.data)
        return (
        <div className="AutocompleteText" >
        <div className=''>
          <label
            for="client" > Client </label>
          <Input name="client"
            onChange={
              this.searchData.bind(this)
            }
            placeholder="Rechercher un client"
            value={
              text
            }
            className="form-control" />
          {
            (this.state.list) ?

              <div >
                <ul>
                  {
                    this.state.list.map((value, i) => {
                      return (
                        <li key={i} onClick={
                          () => this.select(value)}> {value.name} : {value.adresse}  </li>
                      )

                    })
                  } </ul> </div> : null}</div></div>
        )
      }
  }
