import React, { Component } from 'react';
import FinalForm from './test_input';
import Client from '../components/Devis/inputClient';
import { Form, Button, notification,  } from 'antd';


var type = ''
var title = ''
var message = ''
var duration = null
let id = 0;

class NotFound extends Component {
    constructor() {
        super();
        this.state = {
            res: [],
            client: [],
            nameSite: '',
            sub: false,
            final: [],
        }
    }

    componentDidMount() {


    }

    add = () => {
        const { form } = this.props;
        // can use data-binding to get
        const keys = form.getFieldValue('keys');
        const nextKeys = keys.concat(id++);
        // can use data-binding to set
        // important! notify form to detect changes
        form.setFieldsValue({
            keys: nextKeys,
        });
    };



    getData(e) {
        this.setState({ client: [...this.state.client, e] })
    }


    handleSubmit(event) {
        const {getFieldValue } = this.props.form;
        const keys = getFieldValue('keys');



            if(this.state.sub !== true && this.state.res[keys.length -1] !== undefined){
                this.Final();
            }



        event.preventDefault();
    }

    getDataForPdf(e) {
        // var array = [];

        // array.push(e)

        // var obj = {};

        // //Delete .service
        // obj.site = e.service;
        // obj.info = e.info;

        this.setState({ res: [...this.state.res, e] })

    }




    Final() {
        var finalData = {}

        finalData.service = this.state.res
        finalData.client = this.state.client

        this.setState({
            sub: true,
            // final : finalData
        }
        )

        // console.log(this.props.text);

        this.props.text(finalData)
        // console.log(this.props.text);
        this.props.history.push('/PDF')

    }




    render() {
        // console.log(this.state.res);

        const { getFieldDecorator, getFieldValue } = this.props.form;
        // const formItemLayout = {
        //     labelCol: {
        //         xs: { span: 24 },
        //         sm: { span: 4 },
        //     },
        //     wrapperCol: {
        //         xs: { span: 24 },
        //         sm: { span: 20 },
        //     },
        // };
        // const formItemLayoutWithOutLabel = {
        //     wrapperCol: {
        //         xs: { span: 24, offset: 0 },
        //         sm: { span: 20, offset: 4 },
        //     },
        // };


        getFieldDecorator('keys', { initialValue: [] });
        const keys = getFieldValue('keys');
        const formItems = keys.map((k, index) => (

            <div key={k}>
                <Form.Item className="row" style={{ marginTop: '1%' }}
                    // {...(index === 0 ? formItemLayout : formItemLayoutWithOutLabel)}
                    // label={index === 0 ? '' : ''}
                    required={false}
                    key={k}
                >
                    {/* {getFieldDecorator(`names[${k}]`, {
                        validateTrigger: ['onChange', 'onBlur'],
                        rules: [
                            {
                                required: true,
                                whitespace: true,
                                message: "Veuillez remplir tout les champs avant de pouvoir créer le devis",
                            },
                        ],
                    })
                        ( */}

                    <div>
                        <hr></hr>
                        <div className="row">
                            <label className="p">Formulaire n° {k + 1}</label>
                            <FinalForm getDataForPdf={this.getDataForPdf.bind(this)} />
                        </div>
                    </div>
                    {/* )} */}
                    {/* {keys.length > 1 ? (
                        <div className="text-center">
                            <p className="p delete">Supprimer le service n° {k + 1} &nbsp;
                  <Icon
                      className="dynamic-delete-button"
                      type="minus-circle-o"
                      onClick={() => this.remove(k)}
                    />
                  </p>
                        </div>
                    ) : null} */}
                </Form.Item>
            </div>

        ));


        if (this.state.res[keys.length - 1 ] !== undefined && this.state.client.length !== 0) {

            if (this.state.sub === true) {
                type = "warning"
                title = "Attention !"
                message = "Votre devis est deja prêt"

            } else {
                type = 'success'
                title = "Visualisation de votre devis"
                message = "Si le devis vous convient vous pouvez le télécharger en cliquant sur le bouton en bas de page"
                duration = 4
            }

        }
        else {
            type = 'info'
            title = "Oups ..."
            message = "Vous devez remplir toute les informations sur la page avant de convertir les données en PDF.."
        }

        const openNotificationWithIcon = (type) => {
            notification[type]({
                message: title,
                description: message,
                duration: duration,
            });
        };


        return (
            <div className="bgForm p-5" style={{ height: '100vh', padding:"50vh"}}>
            <div className="divForm"   style={{height:'90vh',overflowY: "scroll",scrollbarWidth: 'none', borderRadius: "15px", boxShadow: '5px 10px 5px rgba(0,0,0,0.25)' }}>
            <div className="container" style={{ marginTop:"5%"}}   >
            <h4 className="text-center">Création de devis</h4>
                <form onSubmit={this.handleSubmit.bind(this)} >
                    <Client getData={this.getData.bind(this)} />
                    {/* <FinalForm getDataForPdf={this.getDataForPdf.bind(this)} /> */}

                    <div className="">
                        <div className="">
                            {formItems}
                            {/* <button onClick={this.getKey.bind(this)}>getKey</button> */}
                        </div>
                        <div className="text-center">
                            <Button type="dashed" className="btn_add" onClick={this.add} style={{ width: '20%', marginBottom: '2vh', marginTop: '2vh' }}>
                                {/* <Icon type="plus" /> */}
                                Ajouter un nouveau site
                        </Button>
                        </div>
                    </div>
                    <div className="text-center">
                        <input onClick={() => openNotificationWithIcon(type)} className="btn btn-light" type="submit" value="Convertir en PDF" style={{ marginTop: '5%', marginBottom: '5%' , color: 'white', background: 'linear-gradient(90deg, #ff5f6d 0%,#ffc371 100% )' , border: 'linear-gradient(90deg, #ff5f6d 0%,#ffc371 100% )' }} />
                    </div>
                </form>
            </div>
            </div>
            </div>
        )
    }
}
const WrappedDynamicFieldSet = Form.create({ name: 'dynamic_form_item' })(NotFound);
export default WrappedDynamicFieldSet;
