import React, { Component } from 'react';
import { Input } from 'antd';


var data = {};

export default class DetailClient extends Component {
    constructor() {
        super();
        this.state = {
            detailClient: [],
            nameSignataire: '',
            refTech: '',
            contactFact: '',
        }
    }

    nameSignataire(e) {
        this.setState({
            nameSignataire: e.target.value
        })
    }

    refTech(e) {
        this.setState({
            refTech: e.target.value
        })
    }

    contactFact(e) {
        this.setState({
            contactFact: e.target.value
        })
    }

    pushData(){
        data.nameSignataire = this.state.nameSignataire
        data.refTech = this.state.refTech
        data.contactFact = this.state.contactFact

        this.setState({
            detailClient: data
        }
        , () => {
            this.props.getDetails(this.state.detailClient)
        }
        )
    }


    render() {
        const {nameSignataire, refTech, contactFact} = this.state;

        if(nameSignataire !== '' && refTech !== '' && contactFact !== '')
        {

            this.pushData();
        }

        return (
            <div>
                <div className="row" style={{ marginTop: "2vh", marginBottom: "2vh" }}>
                    <div className="col-4">
                        <label className="p">Nom du Signataire : </label>
                        <Input name="nameSignataire"
                            placeholder="Nom du Signataire"
                            value={
                                nameSignataire
                            }
                            onChange={
                                this.nameSignataire.bind(this)
                            }
                            className="form-control" />
                    </div>
                    <div className="col-4">
                        <label className="p">Nom du Référent technique : </label>
                        <Input name="refTech"
                            placeholder="Référent technique"
                            value={
                                refTech
                            }
                            onChange={
                                this.refTech.bind(this)
                            }
                            className="form-control" />
                    </div>
                    <div className="col-4">
                        <label className="p">Nom du Contact pour la facturation : </label>
                        <Input name="contactFact"
                            placeholder="Contact facturation"
                            value={
                                contactFact
                            }
                            onChange={
                                this.contactFact.bind(this)
                            }
                            className="form-control" />
                    </div>
                </div>
            </div>
        );
    }


}
