import React, { Component } from 'react';
import axios from 'axios';
import loader from '../assets/loader.gif';
import './test.css'

export default class App extends Component {
    constructor() {
        super();
        this.state = {
            data: [],
            list: undefined,
            text: "",
        }
    }

    componentDidMount() {
        var data = [];

        axios.get(`https://sayse.pipedrive.com/v1/deals?api_token=d168467b213a71acdc4ba5cb5b2cfe96529ac061&limit=500`)
            .then(res => {

                res.data.data.forEach(element => {
                    data.push(element.org_id)
                });

                data = data.filter((v, i, arr) => arr.indexOf(v) === i)
                this.setState({ data: data })

                // this.setState(() => ({
                //     data: {
                //         ...this.state.data,
                //         org_name: data,
                //     },
                // }))

            });
    }

    select(value) {

        this.setState({ text: value, list: [] })
        return (
            null
        );
    }

    searchData(e) {
        var queryData = [];
        if (e.target.value !== '') {
                for (let i = 0; i < this.state.data.length; i++) {
                    if (this.state.data.length > 2000) {
                        console.log('ok')
                    this.state.data.org_id[i].forEach(function (person_1) {
                        if (person_1 !== null) {
                            if (person_1.toLowerCase().indexOf(e.target.value) !== -1) {
                                if (queryData.length < 10) {
                                    queryData.push(person_1);

                                }
                            }
                        }
                    });

                    // console.log(queryData)
                }
            }
        }

        this.setState({ list: queryData });
        this.setState({ text: e.target.value })

    }
    render() {
        const { text } = this.state

        if (this.state.data.length === 0) {
            return (
                <div className="container text-center" style={{ marginTop: '5%' }}>
                    <img src={loader} alt='Chargement' width='25%'></img>
                </div>
            )
        }

        return (
            <div className="AutocompleteText container">
                <input onChange={this.searchData.bind(this)} placeholder="Rechercher un client" value={text} className="form-control" />
                {(this.state.list) ?
                    <div>
                        <ul>
                            {this.state.list.map((value, i) => {
                                return (
                                    <li key={i} onClick={() => this.select(value)}>
                                        {value}
                                    </li>
                                )
                            })}
                        </ul>
                    </div> : null}
            </div>
        )
    }
}
