import React, { Component } from 'react';
import axios from 'axios';
import loader from '../../assets/loader.gif';
import './devis.css'

export default class Devis extends Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      suggestions: [],
      text: '',
    }
  }

  componentDidMount() {

    axios.get(`https://sayse.pipedrive.com/v1/deals?api_token=d168467b213a71acdc4ba5cb5b2cfe96529ac061&limit=500`)
      .then(res => {
        var data = [];

        res.data.data.forEach(element => {
          data.push(element.person_name)
        });
        // console.log([...new Set(data)]);

        data = data.filter((v, i, arr) => arr.indexOf(v) === i)
        function filterByValue(array, string) {
          return array.filter(o =>
              Object.keys(o).some(k => o[k].toString().toLowerCase().includes(string.toLowerCase())));
      }
        this.setState({ items: filterByValue(data, this.state.text) });
        // this.setState({ items: data })
        console.log(res.data.data)

      });

  }

  onTextChange = (e) => {
    const { items } = this.state

    const value = e.target.value;
    let suggestions = [];

    if (value.length > 0) {
      const regex = new RegExp(`^${value}`, 'i');
      suggestions = items.sort().filter(v => regex.test(v));
    }

    this.setState({ suggestions, text: value })

  }
  renderSuggestions() {
    const { suggestions } = this.state;

    if (suggestions.length === 0) {
      return null;
    }
    return (
      <ul>
        {suggestions.map((item, i) => <li onClick={() => this.suggestionSelect(item)} key={i} >{item}</li>)}
      </ul>
    );
  }

  suggestionSelect(value) {
    this.setState({
      text: value,
      suggestions: [],
    })
  }

  render() {
    const { text } = this.state

    if(this.state.items.length === 0){
      return(
          <div className="container text-center" style={{ marginTop:'5%'}}>
              <img src={loader} alt='Chargement' width='25%'></img>
          </div>
      )
  }
    return (
        <div style={{ marginTop:'2%'}}>
              <label for="client"><h6>Choisir un client</h6></label>
              <div className='AutocompleteText'>
                <input name="client" value={text} className="form-control" onChange={this.onTextChange} type="text" />
                <ul>
                  {this.renderSuggestions()}
                </ul>
              </div>
              <button type="submit" className="btn btn-primary" style={{ marginTop:'2%'}}>Valider</button>
        </div>
    );
  }
}
