import React, {
  Component
} from 'react';
import axios from 'axios';
import loader from '../../assets/loader.gif';
import './devis.css'
import {
  Input
} from 'antd';

export default class InputClient extends Component {
  constructor() {
    super();
    this.state = {
      data: {},
      list: undefined,
      text: "",
      i: 0,
      finish: false,
      address: "",
    }
  }

  componentDidMount() {
    var data = [];

    var start = 0;
    var { i } = this.state
    for (i = 0; i < 10; i++) {
      axios.get(`https://sayse.pipedrive.com/v1/deals?api_token=d168467b213a71acdc4ba5cb5b2cfe96529ac061&start=` + start + `&limit=500`)
        .then(res => {
          // console.log(`https://sayse.pipedrive.com/v1/deals?api_token=d168467b213a71acdc4ba5cb5b2cfe96529ac061&start=` + start + `&limit=500`)

          if (res.data.data !== null) {

            res.data.data.forEach(element => {
              if (element.org_id === null) {
                return false;
              }
              else {

                // data.push(element.org_id)
                data.push({ name: element.org_id.name, adresse: element.org_id.address });

                // console.log(element.org_id)
                //   this.setState(() => ({
                //     data: {
                //         ...this.state.data,
                //         org_name: element.org_id,
                //     },
                // }))

              }
            });

          }
          if (i === 10) {
            // console.log(this.getUnique(data,'name'))
            this.setState({ data: this.getUnique(data, 'name') })
            console.log(this.state.data)
            return;
          }


        })
      start += 500;
    }




  }


  getUnique(arr, comp) {

    const unique = arr
      .map(e => e[comp])

      // store the keys of the unique objects
      .map((e, i, final) => final.indexOf(e) === i && i)

      // eliminate the dead keys & store unique objects
      .filter(e => arr[e]).map(e => arr[e]);

    return unique;

  }


  select(value) {

    this.setState({
      text: value.name,
      address: value.adresse,
      list: []
    })

    return (
      null
    );
  }

  searchData(e) {
    // console.log(this.state.data.length)
    var err = [{
        name:"La recherche n'a retourner aucun resultat", 
        adresse: '',
    }]

    var queryData = [];
    if (e.target.value !== '') {
      // this.state.data.forEach(function (person) {
      for (let i = 0; i < this.state.data.length; i++) {
        // console.log(this.state.data[i].name);

        if (this.state.data[i].name !== null) {
          if (this.state.data[i].name.toLowerCase().indexOf(e.target.value) !== -1) {


            if (queryData.length < 10) {
              queryData.push(this.state.data[i]);
            }
          }
        }
      }

      // });

    }
    this.setState({
      list: queryData
    });
    this.setState({
      text: e.target.value
    })

  }
  searchAddress(e) {
    this.setState({
      address: e.target.value
    })
  }
  render() {
    const { text } = this.state
    const { address } = this.state

    var { data } = this.state
    // console.log(data)


    if (data.length < 1000 || data.length === undefined) {
      return (
        <div className="container text-center" style={{ marginTop: '5%' }}>
          <img src={loader} alt='Chargement' width='100%'></img>
        </div>
      )
    }

    else {
      return (<div className="AutocompleteText" >
        <div className=''>
          <label
            for="client" > Client </label>
          <Input name="client"
            onChange={
              this.searchData.bind(this)
            }
            placeholder="Rechercher un client"
            value={
              text
            }
            className="form-control" /> {
            (this.state.list) ?
              <div >
                <ul> {
                  this.state.list.map((value, i) => {
                    if(value === undefined)
                    {
                      console.log('vide')
                    }
                    return (
                      <li key={i} onClick={
                        () => this.select(value)}> {value.name} : {value.adresse}  </li>
                    )

                  })
                } </ul> </div> : null}</div><br></br>
        <div className=''>
          <Input name="address"

            placeholder="Rechercher une addresse"
            value={
              address
            }
            onChange={
              this.searchAddress.bind(this)
            }
            className="form-control" />
        </div>
      </div>
      )
    }
  }
}
