import React, { Component } from 'react';
import axios from 'axios';
import loader from '../../assets/loader.gif';


export default class Devis extends Component {
    constructor(props) {
        super(props);
        this.state = {
            client: [],
        }
    }

    componentDidMount() {
        axios.get(`https://sayse.pipedrive.com/v1/deals?api_token=d168467b213a71acdc4ba5cb5b2cfe96529ac061&limit=500`)
            .then(res => {
                var data = [];

                res.data.data.forEach(element => {
                    data.push(element.person_name)
                });
                // console.log([...new Set(data)]);

                data = data.filter((v, i, arr) => arr.indexOf(v) === i)



                this.setState({ client: data })

            });


    }

    render() {
        if(this.state.client.length === 0){
            return(
                <div className="container text-center">
                    <h1>Chargement...</h1>
                </div>
            )
        }
        else{
            return (
                <div className="container text-center">
                    <ul>
                        {
                            this.state.client.map(function (item, i) {
                                if (item !== null) {
                                    return <li style={{ listStyleType: "none" }} key={i}>{item}</li>;
                                }
                            })
                        }
                    </ul>
                </div>
            )
        }


    }
}
