import React, { Component } from 'react';
import data from '../assets/codebeautify.json'
import { Preview, print } from 'react-html2pdf';
import logo from '../assets/logo_sayse.png';
import { Table, Divider, Tag, Icon } from 'antd';

import jsPDF from 'jspdf'
import html2canvas from 'html2canvas';

import printJS from 'print-js'
import { CSVLink } from "react-csv";


var o = {};
var e = {};

export default class GetData extends Component {

    constructor() {
        super();
        this.state = {
            client: [],
            service: [],
            priceAbo: [],
            priceFAS: [],
        }


    }


    componentDidMount() {
        var array = [];

        if (this.props.text.length === 0) {
            this.props.history.push('/ok')
        }
        else {
            this.setState({
                client: [this.props.text.client[0]]
            })

            for (let i = 0; i < this.props.text.service.length; i++) {
                array.push(this.props.text.service[i])
            }
            if (array.length !== 0) {
                this.setState({
                    service: array
                })

                array.map((c,i) => {
                    //detail site
                    console.log(c.service.livraisonAdresse);

                    c.service.map((data,index) =>{

                        //valeur service
                        console.log(data[Object.keys(data)].Abo_mois_ht);

                        //Quantité
                        console.log(Object.keys(data)[0]);

                    })
                })

            }

        }
    }

    download(e) {
        print('Devis', 'PdfDevis')

        e.preventDefault();
    }


    // }
    // // This one lets you improve the PDF sharpness by scaling up the HTML node tree to render as an image before getting pasted on the PDF.
    // print() {
    //     let dataPdf = document.getElementById('contentToPrint');
    //     const pdf = new jsPDF('p', 'pt', 'a4');

    //      html2canvas(dataPdf).then((canvas) => {
    //        let img = canvas.toDataURL('image/jpg');
    //        pdf.addImage(img, 'jpg', 40, 90, 515, 600); //sizings here
    //        pdf.save('web.pdf');
    //      })
    // }

    // print()
    // {
    //     var objectConf = {
    //         css: './pdf.css',
    //         documentTitle: 'testpdf'
    //     }

    //     printJS('printJS-form', 'html', objectConf)
    // }


    print() {
        var btn = document.getElementById("BtnExport");

        btn.style.display = "none";

        // document.URL = 'test';
        // var url = document.URL


        // alert(url)
        // window.history.pushState("object or string", "Title", ".");
        // window.history.pushState({}, null, ".");
        // window.location = "http://www.yourdomain.com/somepage.html";

        // var newurl = "http://www.yourdomain.com/somepage.html"

        window.print();

        setTimeout(function () {
            btn.style.display = "block";
        }, 100);

        // alert("Taille du contenu" + document.getElementById("content").scrollWidth + "/" + document.getElementById("content").scrollHeight + "Taille d'une feulle A4 794 x 1123")
        // alert(Math.floor(document.getElementById("content").scrollHeight / 1123))
    }

    render() {
        // style={{  margin: 0 , padding:0 ,height:'100%',width:'100%', position: 'absolute'}}
        // console.log(this.state.service);

        var priceForSite = ''
        var FASForSite = ''
        var quantité = ''

        var arr = []
        var FASFinal = []
        var quant = []

        var site = []
        var site2 = []
        var nomdusite = []





        var headers = [
            { label: "First Name", key: "firstname" },
            { label: "Last Name", key: "lastname" },
            { label: "Email", key: "email" }
          ];

          var data = [
            { firstname: "Ahmed", lastname: "Tomi", email: "ah@smthing.co.com" },
            { firstname: "Raed", lastname: "Labes", email: "rl@smthing.co.com" },
            { firstname: "Yezzi", lastname: "Min l3b", email: "ymin@cocococo.com" }
          ];








        this.state.service && this.state.service.map((content, i) => {

            // console.log(content);



            nomdusite.push(content.service.nameSite)
            site.push(content.service.nameSite)



            content.service.map((c, index) => {
                // console.log(c);

                var key = content.service.nameSite
                var valueQuant = parseInt(Object.keys(c)[0]);
                var value = parseInt(c[Object.keys(c)].Abo_mois_ht.substring(0, c[Object.keys(c)].Abo_mois_ht.length - 2) * valueQuant);
                var valueFAS = parseInt(c[Object.keys(c)].FAS_HT.substring(0, c[Object.keys(c)].FAS_HT.length - 2) * valueQuant );



                priceForSite = {
                    [key]: value
                }

                FASForSite = {
                    [key]: valueFAS
                }
                quantité = {
                    [key]: valueQuant
                }

                // console.log(Object.keys(c)[0]);

                // console.log(c[Object.keys(c)].Abo_mois_ht.substring(0, c[Object.keys(c)].Abo_mois_ht.length - 2));


                arr.push(priceForSite)
                FASFinal.push(FASForSite)
                quant.push(quantité)



            }
            )

    }
        )
        // console.log(nomdusite);
        // console.log(quant);

        if (arr.length !== 0) {
            // console.log(arr);
            // console.log(FASFinal);
            // console.log(quant);

            for (var i = 0, l = arr.length; i < l; i++) {
                var key = Object.keys(arr[i]);
                if (!o[key]) {
                    o[key] = [];
                }
                o[key].push(arr[i][key]);
            }

            for (var ind = 0, le = FASFinal.length; ind < le; ind++) {
                var keys = Object.keys(FASFinal[ind]);
                if (!e[keys]) {
                    e[keys] = [];
                }
                e[keys].push(FASFinal[ind][keys]);
            }



            var result = []
            site.forEach(element => {
                result.push(o[element].reduce((a, b) => a + b))
            });

            var resultFas = []
            site.forEach(element => {
                resultFas.push(e[element].reduce((a, b) => a + b))
            });


            var total = 0;
            if (result.length !== 0 && resultFas !== 0) {
                this.state.service && this.state.service.map((content, i) =>

                    total += parseInt(result[i]) + parseInt(resultFas[i])
                )


            }

        }


        return (
            <div className="dont-break" id="content">
                <div className="example-config text-center " id="BtnExport">
                    <button className="btn btn-success" onClick={this.print} >Export</button>
                    <CSVLink data={data} headers={headers}>
                        Download CSV
                    </CSVLink>
                </div>




                <div className="container" id="printJS-form" style={{ padding: '3%' }}>
                    <div className="row" style={{ borderBottom: '1px solid #6a9cd0' }}>
                        <div className="col-4">
                            <img src={logo} alt="logo_sayse" width="40%"></img>
                        </div>
                        <div className="col-4 text-center text-danger mt-3">
                            <p className="p_head" style={{ fontWeight:'' }}>DEVIS POUR SOLUTION</p>
                        </div>
                        <div className="col-4 text-center text-danger mt-3">
                            <p className="p_head" style={{ fontWeight:'' }}>SDWAN / ACCES INTERNET <br></br>(By SAYSE)</p>
                        </div>
                    </div>
                    {/* <hr></hr> */}

                    <div className="row" style={{ borderBottom: '0.5px solid #dbdbdb' }}>
                        <div className="col-8" style={{ marginTop: "1%"}}>
                            <p style={{ fontSize: '7px' }}>
                                SAYSE SAS au capital social de 17 566 Euros<br></br>
                                RCS de Paris 801 891 722 00010<br></br>
                                N° de TVA Intracommunautaire FR 03 801 891 722<br></br>
                                Siège social : 10 rue de la Paix - 75002 PARIS<br></br>
                                Etablissement secondaire : 86-88 rue Thiers - 92100 Boulogne Billancourt<br></br>
                                Tel : 0820.432.000 - www.sayse.fr - contact@sayse.fr<br></br>
                            </p>
                        </div>
                        <div className="col-4" style={{ marginTop: "2%" }}>
                            {this.state.client && this.state.client.map((c, i) =>

                                <div className="">
                                    <div key={i}>
                                        {
                                            <li style={{ listStyleType: 'none', fontSize: '7px' }}>
                                                <ul className="text-center" style={{ backgroundColor: '#dbdbdb', color: 'white', borderRadius: '2vh', }}>Date de commande : {c.dateCom.split("-").reverse().join("-")} </ul>
                                                <ul className="text-center" style={{ backgroundColor: '#dbdbdb', color: 'white', borderRadius: '2vh', }}>Durée de souscription : {c.dureSous} mois</ul>
                                            </li>
                                        }
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>
                    {/* <hr></hr> */}
                    <div className="row" style={{ borderBottom: '0.5px solid #dbdbdb' }}>
                        <div className="col-7"></div>
                        <div className="col-5">
                            {this.state.client && this.state.client.map((c, i) =>
                                <div className="row">
                                    <div className="col-5"><p style={{ fontSize: '9px',fontWeight:'bold', borderBottom: '1px solid #f2f2f2',  display: "inline-block", marginTop: "3.5vh" }}>Préparé pour :</p></div>


                                    <div key={i} style={{ padding: "1%", marginTop:"3%" }} className="col-7" >
                                        {
                                            <li style={{ listStyleType: 'none' }}>
                                            <div className="form-inline">
                                                <p style={{ fontSize: '8px', fontWeight:'bold' }}>Client :</p>&nbsp;<p style={{ fontSize:"8px", fontWeight:'bold'}}>{c.client}</p>
                                            </div>
                                            <div className="form-inline">
                                                <p style={{ fontSize: '8px', fontWeight:'bold' }}>Adresse:</p>&nbsp;<p style={{ fontSize:"8px"}}>{c.adresse}</p>
                                            </div>
                                            <div className="form-inline">
                                                <p style={{ fontSize: '8px', fontWeight:'bold' }}>Signataire - représentant légal : </p>&nbsp;<p style={{ fontSize:"8px"}}>{c.nameSignataire}</p>
                                            </div>
                                            <div className="form-inline">
                                                <p style={{ fontSize: '8px', fontWeight:'bold' }}>Référent technique : </p>&nbsp;<p  style={{ fontSize:"8px"}}>{c.refTech}</p>
                                            </div>
                                            <div className="form-inline">
                                                <p style={{ fontSize: '8px', fontWeight:'bold' }}>Contact facturation : </p>&nbsp;<p style={{ fontSize:"8px"}}>{c.contactFact}</p>
                                            </div>
                                            </li>
                                        }
                                    </div>
                                </div>
                            )}
                        </div>
                        {/* <div className="col-2"></div> */}

                    </div>
                    {/* <hr></hr> */}



                    {/* {this.state.test_service && this.state.test_service.map((c, i) =>
                                    <div className="" key={i}>
                                            <p>MasqueReseau1 : {c.MasqueReseau1}</p>    
                                            <p>MasqueReseau2 : {c.MasqueReseau2}</p>    
                                            <p>ipLan : {c.ipLan}</p>
                                            <p>iWLan : {c.iWLan}</p>
                                            <p>livraisonAdresse : {c.livraisonAdresse}</p>                                
                                            <p>mail : {c.mail}</p>                                
                                            <p>name : {c.name}</p>                                
                                            <p>nameSite : {c.nameSite}</p>                                
                                            <p>tel : {c.tel}</p>      

                                         <hr></hr>
                                    </div>

                                )} */}

                    <div className="mt-3">
                        {this.state.service && this.state.service.map((content, i) =>


                            <div className="" key={i} style={{ fontSize: "8px" }}>

                                <div className="row">
