                            <div className="col-1"></div>

                        </div>
                    </div>
                </Preview>
                <div className="row">
                    <div className="col-3"></div>
                    <div className="col-6 text-center" style={{ marginTop: '75vh' }}>
                        <button className="degrade-btn btn" onClick={this.download.bind(this)}> Telecharger <Icon type="file-pdf" /> </button>
                    </div>
                    <div className="col-3"></div>
                </div>

            </div>
        );
    }
}

