import React, {
    Component
} from 'react';
import axios from 'axios';
import loader from '../../assets/loader.gif';
import './devis.css'
import {
    Input
} from 'antd';

import obj from '../../assets/data.json'

export default class InputService extends Component {
    constructor() {
        super();
        this.state = {
            data: [],
            list: undefined,
            text: "",
            price_abo: '',
            marge: '',
        }
    }

    componentDidMount() {
        this.setState({ data: obj }, () => {

        })

    }

    searchData(e) {
        // console.log(this.state.data.length)
        // var libraries = data,
        //     searchString = this.state.searchString.trim().toLowerCase();
        // if (searchString.length > 0) {
        //     libraries = libraries.filter(function (i) {
        //         return i.name.toLowerCase().match(searchString);
        //     });
        // }

        var queryData = [];
        if (e.target.value !== '') {
            // this.state.data.forEach(function (person) {
            for (let i = 0; i < this.state.data.length; i++) {

                if (this.state.data[i].Designation_solution !== null) {

                    // if (this.state.data[i].Designation_solution.toLowerCase().indexOf(e.target.value) !== -1) {
                        // var libraries = this.state.data[i].Designation_solution,
                        var text = this.state.text.trim().toLowerCase();
                            if (text.length > 0) {
                                console.log(this.state.data[i].Designation_solution);


                                // this.state.data[i].filter(function (i) {
                                //     return i.Designation_solution.toLowerCase().match(text);
                                // });
                            }



                        if (queryData.length < 15) {
                            queryData.push(this.state.data[i]);
                        }

                    // }

                }
            }

        }

        this.setState({
            list: queryData
        });
        this.setState({
            text: e.target.value
        })

    }

    select(value) {

        this.setState({
            text: value.Designation_solution,
            price_abo: value.Abo_mois_ht,
            marge: value.FAS_HT,
            list: []
        })

        return (
            null
        );
    }


    searchPrice(e) {
        this.setState({
            price_abo: e.target.value
        })
    }


    render() {
        console.log(this.state.data)
        const { text } = this.state
        const { price_abo } = this.state
        const { marge } = this.state

        return (
            <div className="AutocompleteText" >
                <div className='row'>
                    <div className="col-6">
                        <label
                            htmlFor="service" > Service </label>
                        <Input name="service"
                            onChange={
                                this.searchData.bind(this)
                            }
                            placeholder="Rechercher un service"
                            value={
                                text
                            }
                            className="form-control" />

                        {
                            (this.state.list) ?

                                <div >
                                    <ul>
                                        {
                                            this.state.list.map((value, i) => {
                                                // console.log(value)
                                                return (
                                                    <li key={i} onClick={
                                                        () => this.select(value)}> {value.Designation_solution}  </li>
                                                )

                                            })
                                        } </ul> </div> : null}           </div>
                    <div className="col-6">
                        <label
                            htmlFor="prix_abo" > Prix </label>
                        <Input name="prix_abo"
                            placeholder="prix_abo"
                            value={
                                price_abo
                            }
                            onChange={
                                this.searchPrice.bind(this)
                            }
                            className="form-control" />
                            <p>Marge abo : {marge}</p>
                    </div>
                    </div>
                    </div>
        )
    }
}
