import React, { Component } from 'react';
import axios from 'axios';
import loader from '../../assets/loader.gif';
import { AutoComplete, Button } from 'antd';
import "antd/dist/antd.css";

function onSelect(value) {
  console.log(value);
}

export default class Devis extends Component {
    constructor(props) {
        super(props);
        this.state = {
            client: [],
            dataSource: [],

        }
    }

    componentDidMount() {
        axios.get(`https://sayse.pipedrive.com/v1/deals?api_token=d168467b213a71acdc4ba5cb5b2cfe96529ac061&limit=500`)
            .then(res => {
                var data = [];

                res.data.data.forEach(element => {
                    data.push(element.person_name)
                });
                // console.log([...new Set(data)]);

                data = data.filter((v, i, arr) => arr.indexOf(v) === i)



                this.setState({ client: data })

            });


    }

    handleSearch = (value) => {
        this.setState({
          dataSource: !value ? [] : [

            this.state.client
          ],
        });
      }

    render() {
        const { dataSource } = this.state;

        if(this.state.client.length === 0){
            return(
                <div className="container text-center" style={{ marginTop:'5%'}}>
                    <img src={loader} alt='Chargement' width='25%'></img>
                </div>
            )
        }
        else{
            return (
                <div className="container text-center" style={{ marginTop:'5%'}}>
                    <div className="form-group col-md-12">
                    <form>
                        {/* <label for="inputState">Liste des clients</label>
                        <select id="inputState" class="form-control">
                            <option selected>Choisir un client ...</option>
                            {
                            this.state.client.map(function (item, i) {
                                if (item !== null) {
                                    return <option  key={i}>{item}</option>;
                                }
                            })
                        }
                        </select> */}
                    <AutoComplete
                        dataSource={dataSource}
                        style={{ width: 200 }}
                        onSelect={onSelect}
                        onSearch={this.handleSearch}
                        placeholder="Rechercher un client"
                    />
                    </form>
                </div>
                </div>
            )
        }

    }
}
