    //     this.setState({
    //         data_final: obj
    //     }
    //         , () => {
    //             this.props.getDataForPdf(obj)
    //         })
    // }

    render() {
        const { search, value, name , tel, mail, ipWan, ipLan,MasqueReseau1,MasqueReseau2} = this.state

        // if(this.state.service.length !== 0 && this.state.value !== "" && this.state.name !=="" && this.state.tel !=="" && this.state.mail !=="" && this.state.ipWan !=="" && this.state.ipLan !=="" && this.state.MasqueReseau1 !=="" && this.state.MasqueReseau2 !==""){
        //     this.allDataComplete();     
        // }

        return (
            <div className="body">
                <div className="content container">
                    {/* <form onSubmit={this.handleSubmit.bind(this)}> */}
                        {/* <Client getData={this.getData.bind(this)} /> */}
                        <hr></hr>
                        <Service getService={this.getService.bind(this)} />
                        <hr></hr>

                        <div className="row">
                            <div className="col-12">
                                {/* <p className="text-center p text-danger oblg">OBLIGATOIRE !</p> */}

                                <div className="row" style={{ marginBottom:"3%"}}>
                                    <div className="col-5">
                                        <label>Adresse de livraison : </label>
                                        <ReactGoogleMapLoader
                                            params={{
                                                key: API_KEY,
                                                libraries: "places,geocode",
                                            }}
                                            render={googleMaps =>
                                                googleMaps && (
                                                    <div>
                                                        <ReactGooglePlacesSuggest
                                                            autocompletionRequest={{ input: search }}
                                                            googleMaps={googleMaps}
                                                            onSelectSuggest={this.handleSelectSuggest.bind(this)}
                                                        >
                                                            <Input
                                                                type="text"
                                                                value={value}
                                                                placeholder="Search a location"
                                                                onChange={this.handleInputChange.bind(this)}
                                                            />
                                                        </ReactGooglePlacesSuggest>
                                                    </div>
                                                )
                                            }
                                        />
                                    </div>
                                    <div className="col-2">
                                        <label>Nom du contact : </label>
                                        <Input
                                            type="text"
                                            value={name}
                                            placeholder="Nom du contact"
                                            onChange={this.NameChange.bind(this)}
                                        />
                                    </div>
                                    <div className="col-2">
                                        <label>Telephone du contact : </label>
                                        <Input
                                            type="tel"
                                            value={tel}
                                            placeholder="Tel du contact"
                                            onChange={this.TelChange.bind(this)}
                                        />
                                    </div>
                                    <div className="col-2">
                                        <label>Mail du contact : </label>
                                        <Input
                                            type="email"
                                            value={mail}
                                            placeholder="Mail du contact"
                                            onChange={this.MailChange.bind(this)}
                                        />
                                    </div>

                                </div>
                            </div>
                        </div>
                        <hr></hr>

                        <div className="row" style={{ marginTop:'3%'}}>
                        <div className="col-1"></div>
                            <div className="col-5">
                                <p>Adresse IP WAN</p>
                                <Input
                                    type="text"
                                    value={ipWan}
                                    placeholder="Adresse IP WAN"
                                    onChange={this.ipWanChange.bind(this)}
                                />
                            </div>
                            <div className="col-5">
                                <p>Adresse IP LAN</p>
                                <Input
                                    type="text"
                                    value={ipLan}
                                    placeholder="Adresse IP LAN"
                                    onChange={this.ipLanChange.bind(this)}
                                />
                            </div>
                            <div className="col-1"></div>
                        </div>
                        <div className="row" style={{ marginTop:'3%',marginBottom:'5%'}}>
                        <div className="col-1"></div>
                            <div className="col-5">
                                <p>Masque sous réseau</p>
                                <Input
                                    type="text"
                                    value={MasqueReseau1}
                                    placeholder="Masque sous réseau"
                                    onChange={this.MasqueReseau1.bind(this)}
                                />
                            </div>
                            <div className="col-5">
                            <p>Masque sous réseau</p>
                                <Input
                                    type="text"
                                    value={MasqueReseau2}
                                    placeholder="Masque sous réseau"
                                    onChange={this.MasqueReseau2.bind(this)}
                                />
                            </div>
                            <div className="col-1"></div>
                        </div>

                        <hr></hr>

                        {/* <div className="text-center">
                            <input type="submit" value="Submit" className="degrade-btn btn" style={{ borderRadius: '5%', width: '15vh', marginTop: "3%"}} />
                        </div>
                    </form> */}
                </div>
            </div>
        );
    }
}
