import React, { Component } from 'react';
import obj from '../assets/data.json'


export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
    	data_service: obj
    };
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  addClick(){
    this.setState(prevState => ({ 
    	data_service: [...prevState.data_service, { service: "", prix: "" }]
    }))
  }

  createUI(){
     return this.state.data_service.map((el, i) => (
       <div key={i}>
    	    <input placeholder="First Name" name="firstName" value={el.firstName ||''} onChange={this.handleChange.bind(this, i)} />
    	    <input type='button' value='remove' onClick={this.removeClick.bind(this, i)}/>
       </div>          
     ))
  }

  handleChange(i, e) {
  	 const { name, value } = e.target;
     let data_service = [...this.state.data_service];
     data_service[i] = {...data_service[i], [name]: value};
     this.setState({ data_service });
  }

  removeClick(i){
     let data_service = [...this.state.data_service];
     data_service.splice(i, 1);
     this.setState({ data_service });
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + JSON.stringify(this.state.data_service));
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
          {this.createUI()}        
          <input type='button' value='add more' onClick={this.addClick.bind(this)}/>
          <input type="submit" value="Submit" />
      </form>
    );
  }
}
