import React, {
  Component
} from 'react';
import axios from 'axios';
import loader from '../../assets/loader.gif';
import './devis.css'
import {
  Input, Switch, notification, DatePicker,Select
} from 'antd';


import ReactGoogleMapLoader from "react-google-maps-loader"
import ReactGooglePlacesSuggest from "react-google-places-suggest"

const API_KEY = "AIzaSyAQU3x2PDxhLZEYFk4Af4HBPYDpy40aHEs"

const { Option } = Select;



var title = ''
var message = ''
var type = '';

const openNotificationWithIcon = (type) => {
  notification[type]({
    message: title,
    description: message,
    durarion: null
  });
};

export default class InputClient extends Component {
  constructor() {
    super();
    this.state = {
      data: {},
      list: undefined,
      text: "",
      i: 0,
      finish: false,
      address: "",
      scroll: { overflowY: 'scroll'},

      nameSignataire: '',
      refTech: '',
      contactFact: '',

      data_final: [],
      switchBool: false,

      dateCommande: '',
      dureSous: '',
      idPipe: '',


      value: "",
      search: "",

      typeService: "",

      file: [],
      mailContact: "",
    }


    this.FileChange = this.FileChange.bind(this);

  }

  componentDidMount() {
    var data = [];

    var start = 0;
    var { i } = this.state
    for (i = 0; i < 10; i++) {
      axios.get(`https://sayse.pipedrive.com/v1/deals?api_token=d168467b213a71acdc4ba5cb5b2cfe96529ac061&start=` + start + `&limit=500`)
        .then(res => {
          // console.log(`https://sayse.pipedrive.com/v1/deals?api_token=d168467b213a71acdc4ba5cb5b2cfe96529ac061&start=` + start + `&limit=500`)

          if (res.data.data !== null) {

            res.data.data.forEach(element => {
              if (element.org_id === null) {
                return false;
              }
              else {

                // data.push(element.org_id)
                data.push({ name: element.org_id.name, adresse: element.org_id.address });

                // console.log(element.org_id)
                //   this.setState(() => ({
                //     data: {
                //         ...this.state.data,
                //         org_name: element.org_id,
                //     },
                // }))

              }
            });

          }
          if (i === 10) {
            // console.log(this.getUnique(data,'name'))
            this.setState({ data: this.getUnique(data, 'name') })
            // console.log(this.state.data)
            return;
          }


        })
      start += 500;
    }
  }


  getUnique(arr, comp) {

    const unique = arr
      .map(e => e[comp])

      // store the keys of the unique objects
      .map((e, i, final) => final.indexOf(e) === i && i)

      // eliminate the dead keys & store unique objects
      .filter(e => arr[e]).map(e => arr[e]);

    return unique;

  }


  select(value) {

    this.setState({
      text: value.name,
      address: value.adresse,
      list: [],
      scroll: { overflowY: 'scroll' }
    }
    )

    return (
      null
    );
  }

  searchData(e) {
    // console.log(this.state.data.length)

    // var err = [{
    //   name: "La recherche n'a retourner aucun resultat",
    //   adresse: '',
    // }]

    var queryData = [];
    if (e.target.value !== '') {
