import React, { Component } from 'react';
import Client from '../components/Devis/inputClient';
import Service from './test.js';
import { Input } from 'antd';

import ReactGoogleMapLoader from "react-google-maps-loader"
import ReactGooglePlacesSuggest from "react-google-places-suggest"

const API_KEY = "AIzaSyAQU3x2PDxhLZEYFk4Af4HBPYDpy40aHEs"

export default class Test extends Component {
    constructor() {
        super();
        this.state = {
            client: [],
            service: [],
            result: [],
            key: [],

        }
    }

    componentDidMount() {
        // console.log(this.state.service);
        // console.log(this.state.client);

    }

    handleSubmit(e) {
        // alert('Client : ' + this.state.client.name + ' Adresse : ' + this.state.client.adresse + ' Service : ' + this.state.service.Designation_solution);
        // console.log(this.state.client)
        // console.log(this.state.service)
        console.log(this.state.service);
        console.log(this.state.client);

        e.preventDefault();
    }

    handleInputChange(e) {
        console.log(e.target.value);
    }

    getData(e) {
        // this.setState({client: e})
        this.setState({ client: [... this.state.client, e] })

        // console.log(e);
    }
    getService(e) {
        this.setState({ service: [... this.state.service, e] })
        // console.log(this.state.service);
    }

    adresseChange() {

    }

    render() {
        return (
            <div className="body">
                <div className="content container">
                    <form onSubmit={this.handleSubmit.bind(this)}>
                        <Client getData={this.getData.bind(this)} />
                        <hr></hr>
                        <Service getService={this.getService.bind(this)} />
                        <hr></hr>

                        <div className="row">
                            <div className="col-12">
                                <p className="text-center p text-danger">IMPORTANT !</p>
                                <label>
                                    Adresse Livraison:
                                    <ReactGoogleMapLoader
                                        params={{
                                            key: API_KEY,
                                            libraries: "places,geocode",
                                        }}
                                        render={googleMaps =>
                                            googleMaps && (
                                                <div >
                                                    <ReactGooglePlacesSuggest
                                                        autocompletionRequest={{ input: search }}
                                                        googleMaps={googleMaps}
                                                        onSelectSuggest={this.handleSelectSuggest.bind(this)}
                                                        textNoResults={"Aucune adresse trouvée.."}
                                                    >
                                                        <h6>Adresse de départ</h6><br></br>
                                                        <Input
                                                            type="text"
                                                            value={value}
                                                            placeholder="Départ"
                                                            onChange={this.adresseChange.bind(this)}
                                                            style={{ width: '30vh' }}
                                                        />
                                                        {/* &nbsp;&nbsp;<i class="fas fa-times" onClick={this.delete_1.bind(this)} style={{ color: 'red' }}></i> */}
                                                    </ReactGooglePlacesSuggest>
                                                </div>
                                            )


                                        }
                                    />
                                </label>



                            </div>
                        </div>

                        <hr></hr>








                        <div className="text-center">
                            <input type="submit" value="Submit" className="btn-info" style={{ borderRadius: '5%', width: '15vh' }} />
                        </div>
                    </form>
                </div>
            </div>
        );
    }
}
