import React, {
  Component
} from 'react';
import axios from 'axios';
// import loader from '../../assets/loader.gif';
import './devis.css'
import {
  Input
} from 'antd';

export default class InputClient extends Component {
  constructor() {
    super();
    this.state = {
      data: [],
      list: undefined,
      text: "",
      finish: null,
    }
  }

  componentDidMount() {
    var data = [];

    var start = 0;

    for (let i = 0; i < 15; i++) {
      axios.get(`https://sayse.pipedrive.com/v1/deals?api_token=d168467b213a71acdc4ba5cb5b2cfe96529ac061&start=` + start + `&limit=500`)
        .then(res => {

          if (res.data.data !== null) {
            res.data.data.forEach(element => {
              data.push(element.org_id)
              data = data.filter((v, i, arr) => arr.indexOf(v) === i)
            });
            console.log(data)
          }
        })
      start += 500;
    }


  }


  select(value) {
    // e.preventDefault();
    // console.log(value)

    this.setState({
      text: value,
      list: []
    })

    return (
      null
    );
  }

  searchData(e) {
    // console.log(this.state.data)

    var queryData = [];
    if (e.target.value !== '') {
      this.state.data.forEach(function (person) {
        if (person !== null) {
          if (person.toLowerCase().indexOf(e.target.value) !== -1) {
            if (queryData.length < 10) {
              queryData.push(person);
            }
          }
        }

      });

    }
    this.setState({
      list: queryData
    });
    this.setState({
      text: e.target.value
    })

  }
  render() {
    const {
      text
    } = this.state

    // if (this.state.data.length === 0) {
    //   return (
    //     <div className="container text-center" style={{ marginTop: '5%' }}>
    //       <img src={loader} alt='Chargement' width='25%'></img>
    //     </div>
    //   )
    // }

    return ( <
      div className = "AutocompleteText" >
      <
      label
      for = "client" > Client < /label> <
      Input name = "client"
      onChange = {
        this.searchData.bind(this)
      }
      placeholder = "Rechercher un client"
      value = {
        text
      }
      className = "form-control" / > {
        (this.state.list) ?
        <
        div >
        <
        ul > {
          this.state.list.map((value, i) => {
            return ( <
              li key = {
                i
              }
              onClick = {
                () => this.select(value)
              } > {
                value
              } <
              /li>
            )
          })
        } <
        /ul> <
        /div> : null} <
        /div>
      )
    }
  }
