import React, { Component } from 'react';
import axios from 'axios';
import { AutoComplete, Button } from 'antd';
import "antd/dist/antd.css";

function onSelect(value) {
  console.log(value);
}

export default class Complete extends React.Component {
  state = {
    client: [],
    dataSource: [],
    search: '',
  }

  componentDidMount() {
