import React, { Component } from 'react';
import Client from '../components/Devis/inputClient';
import Service from './test.js';


export default class Test extends Component {
    constructor() {
        super();
        this.state = {
            client: [],
            service: [],
            result: [],
            key: [],

        }
    }

    componentDidMount(){
        // console.log(this.state.service);

    }

    handleSubmit(e) {
        // alert('Client : ' + this.state.client.name + ' Adresse : ' + this.state.client.adresse + ' Service : ' + this.state.service.Designation_solution);
        // console.log(this.state.client)
        // console.log(this.state.service)
        this.getKey();
        console.log(this.state.service);

        e.preventDefault();
    }

    handleInputChange(e){
        console.log(e.target.value);
    }

    getData(e){
        this.setState({client: e})
        // console.log(e);
    }
    getService(e){
        this.setState({service:[... this.state.service, e]})
        // console.log(this.state.service);
    }

    getKey(keys){
        this.setState({key: keys})
        // console.log(this.state.key);
    }


    render() {
        return (
            <div className="body">
                <div className="content container">
                    <form onSubmit={this.handleSubmit.bind(this)}>
                        <Client getData={this.getData.bind(this)}/>
                        <hr></hr>
                        <Service getService={this.getService.bind(this)} getKey={this.getKey.bind(this)}/>
                        <div className="text-center">
                            <input type="submit" value="Submit" className="btn-info"/>
                        </div>
                    </form>
                </div>
            </div>
        );
    }
}
