  render() {
    return (
      <div className="App">
        <h1>The form</h1>
        <label>Adresse</label>
        <hr />

        {
          this.state.countries.map((country, index) => {
            return (
              <div key={index}>
                <input type="text" onChange={(e) => this.handleChange(e, index)} value={country} />
                <button onClick={() => this.handleRemove(index)}>Remove</button>
              </div>
            );
