import React, {
  Component
} from 'react';
import axios from 'axios';
// import loader from '../../assets/loader.gif';
import './devis.css'
import {
  Input
} from 'antd';

export default class InputClient extends Component {
  constructor() {
    super();
    this.state = {
      data: {},
      list: undefined,
      text: "",
      i: 0,
      finish: false,
    }
  }

  componentDidMount() {
    var data = [];

    var start = 0;
    var {i} = this.state
    for (i = 0; i < 10; i++) {
      axios.get(`https://sayse.pipedrive.com/v1/deals?api_token=d168467b213a71acdc4ba5cb5b2cfe96529ac061&start=` + start + `&limit=500`)
        .then(res => {

          if (res.data.data !== null) {
            res.data.data.forEach(element => {
              if (element.org_id === null) {
                return false;
              }
              else {

                data.push(element.org_id)
                // console.log(element.org_id)
              //   this.setState(() => ({
              //     data: {
              //         ...this.state.data,
              //         org_name: element.org_id,
              //     },
              // }))

              }
            });
          }
          if(i === 10)
          {
            this.setState({data: data})

            for (let i = 0; i < this.state.data.length; i++) {
              this.state.data = this.state.data.filter((v, i, arr) => arr.indexOf(v) === i)
            }

          }
        })
      start += 500;
    }



  }

  select(value) {

    this.setState({
      text: value.name,
      list: []
    })

    return (
      null
    );
  }

  searchData(e) {
    console.log(this.state.data.length)

    var queryData = [];
    if (e.target.value !== '') {
      // this.state.data.forEach(function (person) {
        for (let i = 0; i < this.state.data.length; i++) {
          // console.log(this.state.data[i].name);

        if (this.state.data[i].name !== null) {
          if (this.state.data[i].name.toLowerCase().indexOf(e.target.value) !== -1) {
            if (queryData.length < 10) {
              queryData.push(this.state.data[i]);
            }
          }
        }
      }
      // });

    }
    this.setState({
      list: queryData
    });
    this.setState({
      text: e.target.value
    })

  }
  render() {
    const {
      text
    } = this.state
    var { data } = this.state
    // if (data.length === 0) {
    //   return (
    //     <div className="container text-center" style={{ marginTop: '5%' }}>
    //       <img src={loader} alt='Chargement' width='25%'></img>
    //     </div>
    //   )
    // }
  //  data = Object.keys(data).reduce((acc, key) => acc + key + data[key].toString(), '')
    // if(data.length > 0){
    // data.forEach(element => {
    //   console.log(element)
    // });

    return (<div className="AutocompleteText" >
      <label
        for="client" > Client </label>
      <Input name="client"
        onChange={
          this.searchData.bind(this)
        }
        placeholder="Rechercher un client"
        value={
          text
        }
        className="form-control" /> {
        (this.state.list) ?
          <div >
            <ul> {
              this.state.list.map((value, i) => {
                return (
                  <li key={i} onClick={
                    () => this.select(value)}> { value.name } : {value.address}  </li>
                )
              })
            } </ul> </div> : null} </div>
    )
  }
}
