import React, { Component } from 'react';
import axios from 'axios';
import loader from '../../assets/loader.gif';
import { AutoComplete } from 'antd';

function onSelect(value) {
    console.log('onSelect', value);
}

export default class Devis extends Component {
    constructor(props) {
        super(props);
        this.state = {
            client: [],
            dataSource: [],

        }
    }

    componentDidMount() {
        axios.get(`https://sayse.pipedrive.com/v1/deals?api_token=d168467b213a71acdc4ba5cb5b2cfe96529ac061&limit=500`)
            .then(res => {
                var data = [];

                res.data.data.forEach(element => {
                    data.push(element.person_name)
                });
                // console.log([...new Set(data)]);

                data = data.filter((v, i, arr) => arr.indexOf(v) === i)

                this.setState({ client: data })
            });

            function filterByValue(array, string) {
                return array.filter(o =>
                    Object.keys(o).some(k => o[k].toString().toLowerCase().includes(string.toLowerCase())));
            }
            this.setState({ data: filterByValue(response.data, this.state.search) });


    }

    handleSearch = (value) => {
        this.setState({
          dataSource: !value ? [] : [
            value,
            value + value,
          ],
        });
      }

    render() {
        const { dataSource } = this.state;

        if(this.state.client.length === 0){
            return(
                <div className="container text-center" style={{ marginTop:'5%'}}>
                    <img src={loader} alt='Chargement' width='25%'></img>
                </div>
            )
        }
        else{
            return (
                <div className="container text-center" style={{ marginTop:'5%'}}>
                    <div className="form-group col-md-12">
                    <form>
                         <AutoComplete
                            dataSource={dataSource}
                            style={{ width: 200 }}
                            onSelect={onSelect}
                            onSearch={this.handleSearch}
                            placeholder="input here"
                        />
                    </form>
                </div>
                </div>
            )
        }

    }
}
