import React, {Component} from 'react'
import SearchInput, {createFilter} from 'react-search-input'
import axios from 'axios';

// import emails from './mails'

const KEYS_TO_FILTERS = ['user.name', 'subject', 'dest.name']

export default class test extends Component {
  constructor (props) {
    super(props)
    this.state = {
        emails: [],
        searchTerm: ''
    }
    this.searchUpdated = this.searchUpdated.bind(this)
  }

  componentDidMount() {

    axios.get(`https://sayse.pipedrive.com/v1/deals?api_token=d168467b213a71acdc4ba5cb5b2cfe96529ac061&limit=500`)
      .then(res => {
        var data = [];

        res.data.data.forEach(element => {
          data.push(element.person_name)
        });
        // console.log([...new Set(data)]);

        data = data.filter((v, i, arr) => arr.indexOf(v) === i)

        this.setState({ emails: data })
        console.log(res.data.data)

      });

  }

  render () {
    const { emails } = this.state 
    const filteredEmails = emails.filter(createFilter(this.state.searchTerm, KEYS_TO_FILTERS))

    return (
      <div>
        <SearchInput className="search-input" onChange={this.searchUpdated} />
        {filteredEmails.map(email => {
          return (
            <div className="mail" key={email}>
              {/* <div className="from">{email.user.name}</div>
              <div className="subject">{email.subject}</div> */}
            </div>
          )
        })}
      </div>
    )
  }

  searchUpdated (term) {
    this.setState({searchTerm: term})
  }
}
